function F_YF(V, U, Ax, Yaw, SA)		# front tire force
  if isdefined(:N)
		F_YF = zeros(Float64, (N+1,1))
		for i in 1:N+1
			F_YF[i] = ((PD2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff))*sin(PC1*atan((((PK1*sin(2*atan(PK2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff))))/(((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)) + ((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)))/(((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff))^2 + EP^2)^(0.5))*0.001)+EP))*((atan((V[i] + la*Yaw[i])/(U[i]+EP)) - SA[i]) + PH2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff) + PH1)) - ((PE2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff) + PE1)*(1 - PE3)*(((atan((V[i] + la*Yaw[i])/(U[i]+EP)) - SA[i]) + PH2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff) + PH1))/((((atan((V[i] + la*Yaw[i])/(U[i]+EP)) - SA[i]) + PH2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff) + PH1)^2 + EP^2)^(0.5)))*((((PK1*sin(2*atan(PK2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff))))/(((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)) + ((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)))/(((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff))^2 + EP^2)^(0.5))*0.001)+EP))*((atan((V[i] + la*Yaw[i])/(U[i]+EP)) - SA[i]) + PH2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff) + PH1)) - atan((((PK1*sin(2*atan(PK2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff))))/(((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)) + ((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)))/(((PD2*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff))^2 + EP^2)^(0.5))*0.001)+EP))*((atan((V[i] + la*Yaw[i])/(U[i]+EP)) - SA[i]) + PH2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff) + PH1)))))) + (PV2*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)^2 + PV1*(FzF0 - (Ax[i] - V[i]*Yaw[i])*dFzx_coeff)));
		end
	else
		F_YF = Float64
		F_YF = ((PD2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff))*sin(PC1*atan((((PK1*sin(2*atan(PK2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff))))/(((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)) + ((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)))/(((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff))^2 + EP^2)^(0.5))*0.001)+EP))*((atan((V + la*Yaw)/(U+EP)) - SA) + PH2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff) + PH1)) - ((PE2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff) + PE1)*(1 - PE3)*(((atan((V + la*Yaw)/(U+EP)) - SA) + PH2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff) + PH1))/((((atan((V + la*Yaw)/(U+EP)) - SA) + PH2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff) + PH1)^2 + EP^2)^(0.5)))*((((PK1*sin(2*atan(PK2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff))))/(((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)) + ((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)))/(((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff))^2 + EP^2)^(0.5))*0.001)+EP))*((atan((V + la*Yaw)/(U+EP)) - SA) + PH2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff) + PH1)) - atan((((PK1*sin(2*atan(PK2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff))))/(((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)) + ((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)))/(((PD2*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PD1*PC1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff))^2 + EP^2)^(0.5))*0.001)+EP))*((atan((V + la*Yaw)/(U+EP)) - SA) + PH2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff) + PH1)))))) + (PV2*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)^2 + PV1*(FzF0 - (Ax - V*Yaw)*dFzx_coeff)));
	end
	F_YF
end
